mapper.select:
select T1.ORDER_CARD_LIST_ID as "orderCardId", T1.CARD_STATE as "cardState", decode(T2.FACE_VALUE_TYPE,'0','固定','非固定') as "faceValueType", T2.FACE_VALUE/100 as "faceValue", T1.CARDHOLDER_ID as "cardholderId", T1.FIRST_NAME as "cardholderName", T1.EXTERNAL_ID as "externalId", T1.LEG_CARD_NO as "legCardNo", T2.CARD_ISSUE_FEE/100 as "cardIssueFee", T1.CREDIT_AMOUNT/100 as "creditAmount", T1.CARD_NO as "cardNo", T3.check_state as "checkState" from TB_SELL_ORDER_CARD_LIST T1 left join TB_SELL_ORDER_LIST T2 on T1.ORDER_LIST_ID = T2.ORDER_LIST_ID LEFT JOIN TB_CARDHOLDER T3 ON T1.CARDHOLDER_ID = T3.CARDHOLDER_ID WHERE t1.order_id = #orderId:varchar# and t1.DATA_STATE = '1'
and CARD_NO = #{cardNo,jdbcType=VARCHAR}
and CARD_STATE =#{cardState,jdbcType=VARCHAR}
order by t1.card_no
