mapper.select:

select 

from SVC_MNG.TB_CUSTOMER_PERSONAL
where CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
  and SYSTEM_ID = #{systemId,jdbcType=CHAR}

mapper.delete:

delete from SVC_MNG.TB_CUSTOMER_PERSONAL
where CUSTOMER_ID = #{customerId,jdbcType=VARCHAR}
  and SYSTEM_ID = #{systemId,jdbcType=CHAR}
